import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';  // Import useLocation
import './Questions.css'; 

const Result = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const careers = location.state?.careers || [];

    return (
        <div className="result-container">
            <header className="header">
                <h1 className="title">Career Quiz</h1>
                <p className="subtitle">Answer {19 - step + 1} more questions to get your career recommendations!</p>
            </header>
            <h1>ðŸŽ¯ Your Career Recommendations</h1>
            {careers.length > 0 ? (
                <ul>
                    {careers.map((career, index) => (
                        <li key={index}>{career}</li>
                    ))}
                </ul>
            ) : (
                <p>No recommendations found. Please try again.</p>
            )}
            <button onClick={() => navigate('/')}>Restart</button>
        </div>
    );
};

export default Result;
