import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Questions.css';

const Questions = () => {
    const [question, setQuestion] = useState('');
    const [options, setOptions] = useState([]);
    const [selectedOption, setSelectedOption] = useState('');
    const [step, setStep] = useState(1); // Track question number
    const [careerRecommendations, setCareerRecommendations] = useState(null);

    // Fetch first question on load
    useEffect(() => {
        fetchQuestion();
    }, []);

    const fetchQuestion = async () => {
      try {
          const response = await axios.get("http://127.0.0.1:8000/get-question/");
          console.log("Full API Response:", response.data); // Debugging

          const text = response.data.question.content;
          console.log("Extracted Question:", text);

          if (typeof text !== "string") {
              throw new Error("Invalid response format: Expected a string");
          }

          // Extract question and options
          const parsedOptions = text.match(/[A-D]\) (.+)/g) || [];
          setQuestion(text.split("\n")[0]); // First line is the question
          setOptions(parsedOptions);
      } catch (error) {
          console.error("Error fetching question:", error);
      }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedOption) {
        alert("Please select an option!");
        return;
    }

    try {
        const response = await axios.post("http://127.0.0.1:8000/submit-answer/", {
            selected_option: selectedOption
        });

        if (response.data.careers) {
            setCareerRecommendations(response.data.careers);
        } else {
            const text = response.data.question;
            if (typeof text === "string") {
                setQuestion(text.split("\n")[0]);
                setOptions(text.match(/[A-D]\) (.+)/g) || []);
            } else {
                console.error("Invalid question format received:", text);
            }
            setSelectedOption('');
            setStep(step + 1);
        }
    } catch (error) {
        console.error("Error submitting answer:", error);
    }
};

    return (
        <div className="questions-container">
            <header className="header">
                <h1 className="title">Career Quiz</h1>
                <p className="subtitle">Answer {20 - step + 1} more questions to get your career recommendations!</p>
            </header>

            {careerRecommendations ? (
                <div className="results">
                    <h2>ðŸŽ¯ Recommended Careers:</h2>
                    <ul>
                        {careerRecommendations.map((career, index) => (
                            <li key={index}>{career}</li>
                        ))}
                    </ul>
                </div>
            ) : (
                <form className="question-form" onSubmit={handleSubmit}>
                    <h2>{question}</h2>
                    {options.map((option, index) => (
                        <div className="option" key={index}>
                            <input
                                type="radio"
                                id={`option${index}`}
                                name="careerOption"
                                value={option}
                                checked={selectedOption === option}
                                onChange={(e) => setSelectedOption(e.target.value)}
                            />
                            <label htmlFor={`option${index}`}>{option}</label>
                        </div>
                    ))}
                    <button className="submit-button" type="submit">Next</button>
                </form>
            )}
        </div>
    );
};

export default Questions;
