import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';  // Import useLocation
import './Result.css';

const Result = () => {
    const location = useLocation();
    const navigate = useNavigate();
    const careers = location.state?.careers || [];

    return (
        <div className="result-container">
            <header className="header">
                <h1 className="title"> ðŸŽ¯ Your Career Recommendations</h1>
                <p className="subtitle">Based on your answers, here are your best matches:</p>
            </header>
            {careers.length > 0 ? (
                <div className="results-container">
                    {careers.map((career, index) => {
                        const careerDetails = career.split('\n');
                        return (
                            <div className="career-card" key={index}>
                                <h2 className="career-title">{careerDetails[0]}</h2>
                                {careerDetails.slice(1).map((detail, idx) => {
                                    const [sectionTitle, ...contentParts] = detail.split(': ');
                                    const content = contentParts.join(': ');
                                    const bulletListSections = [
                                        "Technology Skills",
                                        "Skills",
                                        "Knowledge",
                                        "Median Wages",
                                        "Projected Job Openings",
                                        "Related Occupations"
                                    ];

                                    const listItems = content.split(',').map((item, i) => (
                                        <li key={i}>{item.trim()}</li>
                                    ));

                                    return (
                                        <div key={idx}>
                                            <h3 className="career-section-title">{sectionTitle}</h3>
                                            {bulletListSections.includes(sectionTitle.trim()) ? (
                                                <ul className="career-section-list">{listItems}</ul>
                                            ) : (
                                                <p className="career-section-content">{content}</p>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        );
                    })}
                    <button className="restart-button" onClick={() => navigate('/')}>Take Quiz Again</button>
                </div>
            ) : (
                <p>No recommendations found. Please try again.</p>
            )}
        </div>
    );
};

export default Result;
